; 0 address offsets in the form 0(a0) are optimized automatically by the assembler
; the value 0 will turn this feature off. This is good for producing a byte-perfect copy of
; the original ROM. When hacking, you don't need this so you can set it to 1 if you want
zeroOffsetOptimization = 0

bugfixes = 0			; if 1, include bug fixes
dezo_steal_fix = 0		; if 1, Shir will no longer steal on Dezo
walk_speed = 0			; 0 = normal; 1 = double; 2 = quadruple
exp_gain = 0			; 0 = normal; 1 = double; 2 = quadruple
meseta_gain = 0			; 0 = normal; 1 = double; 2 = quadruple
checksum_remove = 0		; if 1, remove the checksum calculation routine resulting in a faster boot time
revision = 0			; 0 = Japanese; 1 = first US release; 2 = second US release; 3 = Portuguese
soundrev = 2			; 0 = use sound engine and samples appropriate for revision; 1 = use Japanese audio regardless of revision; 2 = use Japanese audio, but with a quieter snare drum sample; 3 = use US/EU audio regardless of revision
cross_patch = 0			; Set this to 1 to replace the green cross sign with an H and remove the red cross sign, just like
						; the Virtual Console version

	cpu 68000
	include "ps2.macrosetup.asm"
	include "ps2.constants.asm"


StartOfRom:
	if (*) <> 0
		fatal	"Start of ROM is at address $\{*}, but it should be at address 0."
	endif

VectorTable:
	dc.l	System_stack&$FFFFFF, EntryPoint, ErrorTrap, ErrorTrap
	dc.l	ErrorTrap, ErrorTrap, ErrorTrap, ErrorTrap
	dc.l	ErrorTrap, ErrorTrap, ErrorTrap, ErrorTrap
	dc.l	ErrorTrap, ErrorTrap, ErrorTrap, ErrorTrap
	dc.l	ErrorTrap, ErrorTrap, ErrorTrap, ErrorTrap
	dc.l	ErrorTrap, ErrorTrap, ErrorTrap, ErrorTrap
	dc.l	ErrorTrap, ErrorTrap, ErrorTrap, ErrorTrap
	dc.l	HInt, ErrorTrap, VInt, ErrorTrap
	dc.l	ErrorTrap, ErrorTrap, ErrorTrap, ErrorTrap
	dc.l	ErrorTrap, ErrorTrap, ErrorTrap, ErrorTrap
	dc.l	ErrorTrap, ErrorTrap, ErrorTrap, ErrorTrap
	dc.l	ErrorTrap, ErrorTrap, ErrorTrap, ErrorTrap
	dc.l	ErrorTrap, ErrorTrap, ErrorTrap, ErrorTrap
	dc.l	ErrorTrap, ErrorTrap, ErrorTrap, ErrorTrap
	dc.l	ErrorTrap, ErrorTrap, ErrorTrap, ErrorTrap
	dc.l	ErrorTrap, ErrorTrap, ErrorTrap, ErrorTrap

Header:
	if revision=0
	dc.b	"SEGA MEGA DRIVE " ; Console name
	dc.b 	"(C)SEGA 1988.DEC" ; Copyright/Date
	dc.b 	"PHANTASY STAR 2                                 " ; Domestic name
	dc.b 	"PHANTASY STAR 2                                 " ; International name
	dc.b 	"GM 00005501-00"   ; Version

Checksum:
	dc.w	0		; Checksum
	dc.b 	"J               " ; I/O Support
	dc.l 	StartOfRom		; ROM Start

ROMEndLoc:
	dc.l	EndOfRom-1		; ROM End
	dc.l 	RAM_start&$FFFFFF		; RAM Start
	dc.l 	$FFFFFF		; RAM End
	dc.l 	$5241F820		; Backup RAM ID
	dc.l 	$200001		; Backup RAM start address
	dc.l 	$203FFF		; Backup RAM end address
	dc.b 	"            "	; Modem support
	dc.b 	"                                        "	; Notes
	dc.b 	"                " ; Country

	elseif revision=1
	dc.b	"SEGA MEGA DRIVE " ; Console name
	dc.b 	"(C)SEGA 1989.JUN" ; Copyright/Date
	dc.b 	"PHANTASY STAR 2                                 " ; Domestic name
	dc.b 	"PHANTASY STAR 2                                 " ; International name
	dc.b 	"GM 00005501-01"   ; Version

Checksum:
	dc.w	$3792		; Checksum
	dc.b 	"J               " ; I/O Support
	dc.l 	StartOfRom		; ROM Start

ROMEndLoc:
	dc.l	EndOfRom-1		; ROM End
	dc.l 	RAM_start&$FFFFFF		; RAM Start
	dc.l 	$FFFFFF		; RAM End
	dc.l 	$5241F820		; Backup RAM ID
	dc.l 	$200001		; Backup RAM start address
	dc.l 	$203FFF		; Backup RAM end address
	dc.b 	"            "	; Modem support
	dc.b 	"                                        "	; Notes
	dc.b 	"UE              " ; Country
	
	elseif revision=3
	
	dc.b	"SEGA MEGA DRIVE " ; Console name
	dc.b 	"(C)SEGA 1989.JUN" ; Copyright/Date
	dc.b 	"PHANTASY STAR 2                                 " ; Domestic name
	dc.b 	"PHANTASY STAR 2                                 " ; International name
	dc.b 	"GM 00005501-01"   ; Version

Checksum:
	dc.w	$E964		; Checksum
	dc.b 	"J               " ; I/O Support
	dc.l 	StartOfRom		; ROM Start

ROMEndLoc:
	dc.l	EndOfRom-1		; ROM End
	dc.l 	RAM_start&$FFFFFF		; RAM Start
	dc.l 	$FFFFFF		; RAM End
	dc.l 	$5241F820		; Backup RAM ID
	dc.l 	$200001		; Backup RAM start address
	dc.l 	$203FFF		; Backup RAM end address
	dc.b 	"            "	; Modem support
	dc.b 	"                                        "	; Notes
	dc.b 	"UE              " ; Country

	else
	
	dc.b	"SEGA MEGA DRIVE " ; Console name
	dc.b 	"(C)SEGA 1990.JAN" ; Copyright/Date
	dc.b 	"PHANTASY STAR 2                                 " ; Domestic name
	dc.b 	"PHANTASY STAR 2                                 " ; International name
	dc.b 	"GM 00005501-02"   ; Version

Checksum:
	dc.w	$3792		; Checksum
	dc.b 	"J               " ; I/O Support
	dc.l 	StartOfRom		; ROM Start

ROMEndLoc:
	dc.l	EndOfRom-1		; ROM End
	dc.l 	RAM_start&$FFFFFF		; RAM Start
	dc.l 	$FFFFFF		; RAM End
	dc.l 	$5241F820		; Backup RAM ID
	dc.l 	$200001		; Backup RAM start address
	dc.l 	$203FFF		; Backup RAM end address
	dc.b 	"            "	; Modem support
	dc.b 	"                                        "	; Notes
	dc.b 	"UE              " ; Country
	
	endif
EndOfHeader:
	if (*) <> $200
		fatal	"End of header is at address $\{*}, but it should be at address $200. The header + vector table must be exactly 512 bytes long"
	endif

ErrorTrap:
	nop
	nop
	nop

EntryPoint:
	if revision>0
	move.b	(HW_version).l, d0	; get hardware version
	andi.b	#$F, d0		; stored in the lower nibble
	beq.s	SkipSecurity	; branch if console is the first model
	move.l	#'SEGA', (Security_addr).l	; satisfy the TMSS
	endif
SkipSecurity:
	btst	#6, (HW_expansion_control).l
	if checksum_remove=1
	beq.s	ChecksumGood
	else
	beq.s	ChecksumTest
	endif
	cmpi.l	#'init', (Checksum_four_CC).w
	beq.w	GameInit	; branch if checksum routine has already run

ChecksumTest:
	movea.l	#EndOfHeader, a0		; start checking bytes after the header ($200)
	movea.l	#ROMEndLoc, a1		; stop at the end of ROM
	move.l	(a1), d0
	moveq	#0, d1

ChecksumLoop:
	add.w	(a0)+, d1
	cmp.l	a0, d0
	bcc.s	ChecksumLoop
	movea.l	#Checksum, a1	; read the checksum
	cmp.w	(a1), d1			; compare correct checksum to the one in ROM
	bne.w	ChecksumError   ; if they don't match, branch

ChecksumGood:
	lea	(System_stack).w, a6
	moveq	#0, d7

	move.w	#$7F, d6
-
	move.l	d7, (a6)+
	dbf	d6, -

	if revision>0
	move.b	(HW_version).l, d0
	andi.b	#$C0, d0
	move.b	d0, (Graphics_flags).w
	endif
	move.l	#'init', (Checksum_four_CC).w	; Checksum routine successful
GameInit:
	lea	(RAM_start&$FFFFFF).l, a6	; load ram

	moveq	#0, d7
	move.w	#$3F7F, d6
-
	move.l	d7, (a6)+
	dbf	d6, -

	bsr.w	VDPSetupGame
	bsr.w	LoadPCMDrums
	bsr.w	JoypadInit
	move.b	#GameModeID_Sega, (Game_mode_index).w	; start from Sega screen


MainGameLoop:
	move.b	(Game_mode_index).w, d0	; get screen id
	andi.w	#$1C, d0
	lsl.w	#1, d0
	jsr	GameModeTable(pc,d0.w)
	bra.s	MainGameLoop

; ===================================================
; Table for every screen in the game
; ===================================================
GameModeTable:

PtrGameMode_Sega:	    jmp	(GameMode_Sega).l
	nop
PtrGameMode_Title:    jmp	(GameMode_Title).l
	nop
PtrGameMode_Ending:   jmp	(GameMode_Ending).l
	nop
PtrGameMode_Map:    jmp	(GameMode_Map).l
	nop
PtrGameMode_Scene: jmp	(GameMode_Scene).l
	nop
PtrGameMode_Battle:   jmp	(GameMode_Battle).l
	nop
PtrGameMode_Intro:    jmp	(GameMode_Intro).l
	nop
; ===================================================

ChecksumError:
	bsr.w	VDPSetupGame
	move.l	#$C0000000, (VDP_control_port).l	; write to CRAM

	moveq	#$3F, d7
-
	move.w	#$E, (VDP_data_port).l
	dbf	d7, -


ChecksumFailedLoop:
	bra.s	ChecksumFailedLoop


; -----------------------------------------------------------------
RunObjects:
	lea	(Object_RAM).w, a0

	move.w	#$3F, d7
-
	bsr.s	RunObject
	adda.w	#$40, a0		; process next object
	dbf	d7, -

	rts

RunObject:
	move.w	(a0), d0
	beq.w	RunObjectEnd	; return if there is no object at this address
	lea	(ObjectTable-4).l, a1
	lsl.w	#2, d0
	jsr	(a1,d0.w)

	btst	#0, render_flags(a0)
	bne.w	Obj_Delete
	bclr	#7, render_flags(a0)
	btst	#3, render_flags(a0)
	beq.w	loc_3E4
	move.l	x_move_steps(a0), d0
	add.l	d0, x_pos(a0)
	move.l	y_move_steps(a0), d0
	add.l	d0, y_pos(a0)
	move.w	$1C(a0), d0
	move.w	$12(a0), d1
	add.w	d0, d1
	cmpi.w	#$400, d1
	bcs.w	Obj_Delete
	cmpi.w	#$4000, d1
	bcc.w	Obj_Delete
	move.w	d1, $12(a0)
	move.w	x_pos(a0), d0
	move.w	d0, sprite_x_pos(a0)
	move.w	y_pos(a0), d0
	subi.w	#$90, d0
	muls.w	#$400, d0
	divs.w	$12(a0), d0
	neg.w	d0
	addi.w	#$D0, d0
	cmpi.w	#$80, d0
	bls.w	RunObjectEnd
	bmi.w	RunObjectEnd
	cmpi.w	#$180, d0
	bcc.w	RunObjectEnd
	move.w	d0, sprite_y_pos(a0)
	btst	#1, render_flags(a0)
	bne.s	RunObjectEnd
	lea	(Sprite_table_input).w, a1
	moveq	#0, d0
	move.b	$12(a0), d0
	asl.w	#4, d0
	adda.l	d0, a1
-
	cmpi.w	#$E, (a1)
	bcs.s	+
	adda.w	#$10, a1
	bra.s	-

+
	addq.w	#2, (a1)
	moveq	#0, d1
	move.w	(a1), d1
	adda.l	d1, a1
	move.w	a0, (a1)
	bset	#7, render_flags(a0)

RunObjectEnd:
	rts

Obj_Delete:
	movea.l	a0, a1
	moveq	#0, d1
	bra.w	FillMemBlock2	; clear 64 bytes of memory (object size)

loc_3E4:
	move.w	x_pos(a0), d0
	add.w	x_move_steps(a0), d0
	bmi.s	+
	btst	#4, render_flags(a0)
	bne.s	++
	sub.w	(Camera_max_X_pos).w, d0
	bcc.s	++
+
	add.w	(Camera_max_X_pos).w, d0

+
	move.w	d0, x_pos(a0)
	move.w	y_pos(a0), d0
	add.w	y_move_steps(a0), d0
	bmi.s	+
	btst	#4, render_flags(a0)
	bne.s	++
	sub.w	(Camera_max_Y_pos).w, d0
	bcc.s	++
+
	add.w	(Camera_max_Y_pos).w, d0

+
	move.w	d0, y_pos(a0)

; Fix sprite rendering when at the edge of the screen
	if bugfixes=1
	move.w	x_pos(a0), d0
	btst	#4, render_flags(a0)
	bne.s	++
	move.w	d0, d2
	addi.w	#$80, d0
	move.w	(Camera_X_pos_BG).w, d1
	cmp.w	d1, d2
	bge.s	+
	move.w	d1, d3
	lsr.w	#1, d3
	cmp.w	d2, d3
	bgt.s	+++
+
	sub.w	d1, d0
+
	cmpi.w	#$40, d0
	bls.w	RunObjectEnd
	cmpi.w	#$1E0, d0
	bcc.w	RunObjectEnd
	move.w	d0, sprite_x_pos(a0)
	bra.s	SpriteRender_YPos
+
	move.w	(Camera_max_X_pos).w, d2
	sub.w	d2, d1
	sub.w	d1, d0
	cmpi.w	#$40, d0
	bls.w	RunObjectEnd
	cmpi.w	#$1E0, d0
	bcc.w	RunObjectEnd
	move.w	d0, sprite_x_pos(a0)

SpriteRender_YPos:
	move.w	y_pos(a0), d0
	btst	#4, render_flags(a0)
	bne.s	++
	move.w	d0, d2
	addi.w	#$80, d0
	move.w	(Camera_Y_pos_BG).w, d1
	cmp.w	d1, d2
	bge.s	+
	move.w	d1, d3
	lsr.w	#1, d3
	cmp.w	d2, d3
	bgt.s	+++
+
	sub.w	d1, d0
+
	cmpi.w	#$40, d0
	bls.w	RunObjectEnd
	cmpi.w	#$180, d0
	bcc.w	RunObjectEnd
	move.w	d0, sprite_y_pos(a0)
	bra.s	Obj_SpriteTable
+
	move.w	(Camera_max_Y_pos).w, d2
	sub.w	d2, d1
	sub.w	d1, d0
	cmpi.w	#$40, d0
	bls.w	RunObjectEnd
	cmpi.w	#$180, d0
	bcc.w	RunObjectEnd
	move.w	d0, sprite_y_pos(a0)

	else
	move.w	x_pos(a0), d0
	btst	#4, render_flags(a0)
	bne.s	++
	sub.w	(Camera_X_pos_BG).w, d0
	bcc.s	+
	add.w	(Camera_max_X_pos).w, d0
+
	addi.w	#$80, d0

+
	cmpi.w	#$40, d0
	bls.w	RunObjectEnd
	cmpi.w	#$1E0, d0
	bcc.w	RunObjectEnd
	move.w	d0, sprite_x_pos(a0)

	move.w	y_pos(a0), d0
	btst	#4, render_flags(a0)
	bne.s	++
	sub.w	(Camera_Y_pos_BG).w, d0
	bcc.s	+
	add.w	(Camera_max_Y_pos).w, d0
+
	addi.w	#$80, d0

+
	cmpi.w	#$40, d0
	bls.w	RunObjectEnd
	bmi.w	RunObjectEnd
	cmpi.w	#$180, d0
	bcc.w	RunObjectEnd
	move.w	d0, sprite_y_pos(a0)
	endif

Obj_SpriteTable:
	btst	#1, render_flags(a0)
	bne.s	+++	; rts
	lea	(Sprite_table_input).w, a1
	moveq	#0, d0
	btst	#5, render_flags(a0)
	bne.s	+
	move.w	$1E(a0), d0
	subi.w	#$80, d0
	not.w	d0
	andi.w	#$FC, d0
	asl.w	#2, d0
	adda.l	d0, a1
/
	cmpi.w	#$E, (a1)
	bcs.s	+
	adda.w	#$10, a1
	bra.s	-
+
	addq.w	#2, (a1)
	moveq	#0, d1
	move.w	(a1), d1
	adda.l	d1, a1
	move.w	a0, (a1)
	bset	#7, render_flags(a0)
+
	rts
; -----------------------------------------------------------------


BuildSprites:
	move.b	#0, (Link_field_count).w
	move.b	#80, (Sprite_count).w		; sprite limit = 80
	lea	(Sprite_table_buffer).w, a1
	move.l	a1, (Sprite_table_current_entry).w
	lea	(Sprite_table_input).w, a6

	moveq	#$3F, d7
loc_4E4:
	move.w	(a6), d0
	beq.s	loc_512
	moveq	#2, d6
loc_4EA:
	movea.l	a6, a5
	adda.w	d6, a5			; add 2 to address in order to get the pointer of the object
	movea.w	(a5), a0		; load pointer of object
	movea.l	4(a0), a1
	move.w	$24(a0), d0		; Sprite Mappings index
	lsl.w	#1, d0
	adda.w	(a1,d0.w), a1
	tst.b	(a1)
	beq.w	loc_572
	bmi.w	loc_572
	bsr.w	loc_526

loc_50C:
	addq.w	#2, d6		; load next object
	subq.w	#2, (a6)	; decrement object count
	bne.s	loc_4EA

loc_512:
	addq.w	#8, a6
	addq.w	#8, a6
	dbf	d7, loc_4E4

	movea.l	(Sprite_table_current_entry).w, a0		; load sprite table
	move.l	#0, (a0)
	rts

loc_526:
	movea.l	(Sprite_table_current_entry).w, a2		; load sprite table address
	moveq	#0, d1
	move.b	(a1)+, d1
	subq.b	#1, d1		; get number of attributes for each mapping
	move.w	$1E(a0), d2
	move.w	$20(a0), d3

FillSpriteAttributesLoop:
	tst.b	(Sprite_count).w
	beq.s	loc_56C		; branch if there are already 80 sprites
	subq.b	#1, (Sprite_count).w	; otherwise subtract 1 and build this sprite

	move.b	(a1)+, d0
	ext.w	d0
	add.w	d2, d0
	move.w	d0, (a2)+		; set Y position
	move.b	(a1)+, (a2)+	; set sprite size
	addq.b	#1, (Link_field_count).w
	move.b	(Link_field_count).w, (a2)+		; set link field
	move.b	(a1)+, d0
	lsl.w	#8, d0
	move.b	(a1)+, d0
	add.w	8(a0), d0
	move.w	d0, (a2)+		; set art tiles and flags
	move.b	(a1)+, d0
	ext.w	d0
	add.w	d3, d0
	move.w	d0, (a2)+		; set X position
	dbf	d1, FillSpriteAttributesLoop

loc_56C:
	move.l	a2, (Sprite_table_current_entry).w
	rts

loc_572:
	move.w	(a1)+, d0
	move.w	(a1)+, d1
	add.w	$20(a0), d0
	subi.w	#$80, d0
	add.w	$1E(a0), d1
	subi.w	#$80, d1
	andi.w	#$FFF8, d0
	asr.w	#2, d0
	andi.w	#$FFF8, d1
	asl.w	#4, d1
	add.w	d0, d1
	move.l	#$FF6000, d2
	add.w	d1, d2
	move.b	(a1)+, d3
	ext.w	d3
	move.b	(a1)+, d4
	ext.w	d4
-
	movea.l	d2, a5
	move.w	d3, d5
-
	move.w	(a1)+, d0
	beq.s	+
	add.w	8(a0), d0
+
	move.w	d0, (a5)+
	dbf	d5, -

	addi.w	#$80, d2
	dbf	d4, --

	bra.w	loc_50C

; ===========================================================
