; --------------------------------------------------------------
; Object - Blinking red cursor for selection
; --------------------------------------------------------------
red_cursor_orig_y_pos = $2A


Obj_RedCursor:
	move.w	routine(a0), d0
	asl.b	#2, d0
	jsr	RedCursorRoutines(pc,d0.w)
	rts
; --------------------------------------------------------------
RedCursorRoutines:
	bra.w	RedCursor_Init
	bra.w	RedCursor_Main
; --------------------------------------------------------------
RedCursor_Init:
	move.w	y_pos(a0), red_cursor_orig_y_pos(a0)	; save original position
	moveq	#0, d1
	move.b	red_cursor_current_entry(a0), d1	; get number of entries
	lsl.b	#4, d1
	add.w	red_cursor_orig_y_pos(a0), d1
	move.w	d1, y_pos(a0)
	move.b	#$30, render_flags(a0)		; render sprite; absolute screen coordinates; top priority for rendering
	move.w	#$85B6, art_tile(a0)
	move.l	#Map_Cursors, mappings(a0)
	move.w	#1, routine(a0)	; => RedCursor_Main
	; the Japanese version doesn't have the following value in memory cleared which can cause issues
	; when you hold down a directional button when a new cursor is created. An example is a
	; glitch that happens when you order your party, that is if you hold UP while selecting characters,
	; it will corrupt graphics and may cause the game to crash. Just remove the following 'if' condition
	; to fix it for the Japanese version.
	if revision>0
	move.w	#0, (Window_options).w
	endif
; --------------------------------------------------------------
RedCursor_Main:
	tst.w	(Window_queue).w
	bne.s	.f1
	move.w	(Window_index).w, d0
	beq.s	.f1
	tst.w	(Fight_active_flag).w
	bne.s	.f1
	move.w	(Windows_opened_num).w, d0
	subq.w	#1, d0
	lsl.w	#6, d0
	addi.w	#$E000, d0
	cmpa.w	d0, a0
	beq.s	.f2

.f1:
	move.b	#$30, render_flags(a0)			; render sprite; absolute screen coordinates; top priority for rendering
	rts

.f2
	subq.w	#1, anim_frame_timer(a0)			; subtract 1 from timer
	bpl.s	.f3
	move.w	#7, anim_frame_timer(a0)			; blinking timer (alternate between displaying and hiding cursor)
	bchg	#1, render_flags(a0)			; either display or hide cursor
.f3
	moveq	#$10, d2

Cursor_CheckInput:
	moveq	#0, d1
	move.b	red_cursor_current_entry(a0), d1
	move.b	(Joypad_pressed).w, d0
	lsr.b	#1, d0
	bcc.s	.f1		; branch if up was not pressed

; Up Pressed
.b1:
	subq.b	#1, d1
	bpl.s	.f2
	move.b	red_cursor_total_entries(a0), d1
	bra.s	.f2

.f1:
	lsr.b	#1, d0
	bcc.s	.f3		; branch if down was not pressed

; Down Pressed
.b2:
	addq.b	#1, d1
	cmp.b	red_cursor_total_entries(a0), d1
	bls.s	.f2
	moveq	#0, d1

.f2:
	move.b	d1, red_cursor_current_entry(a0)
	mulu.w	d2, d1
	add.w	red_cursor_orig_y_pos(a0), d1
	move.w	d1, y_pos(a0)

.b3:
	move.w	#$1F, anim_timer_start(a0)
	move.w	red_cursor_entries(a0), d0
	move.w	d0, (Window_options).w
	rts

.f3:
	move.b	(Joypad_held).w, d0
	lsr.b	#1, d0
	bcc.s	.f4		; branch if up is not being held down
	subq.w	#1, anim_timer_start(a0)
	bmi.s	.b1
	rts

.f4:
	lsr.b	#1, d0
	bcc.s	.b3		; branch if down is not being held down
	subq.w	#1, anim_timer_start(a0)
	bmi.s	.b2
	rts


; ----------------------------------------------------------------------------
; Object - Tiles used to cover letters in input window (blinking effect)
; ----------------------------------------------------------------------------
input_win_held_timer = $28
input_win_curr_pos = $32

	if revision=0
input_win_char_row = 20-1		; number of characters in a row (starts from 0)
input_win_char_col = 6			; number of columns
	else
input_win_char_row = 18-1		; number of characters in a row (starts from 0)
input_win_char_col = 4			; number of columns
	endif
; ----------------------------------------------------------------------------
Obj_InputWindowCursor:
	move.w	routine(a0), d0
	asl.b	#2, d0
	jsr	InputWindowCursorRoutines(pc,d0.w)
	rts
; ----------------------------------------------------------------------------
InputWindowCursorRoutines:
	bra.w	InputWindowCursor_Init
	bra.w	InputWindowCursor_Main
; ----------------------------------------------------------------------------
InputWindowCursor_Init:
	if revision=0
	move.w	#$F8, x_pos(a0)
	else
	move.w	#$100, x_pos(a0)
	endif
	move.w	#$B0, y_pos(a0)
	move.b	#$30, render_flags(a0)
	move.w	#$8526, art_tile(a0)
	move.l	#Map_Cursors, mappings(a0)
	move.w	#7, anim_frame_timer(a0)
	move.w	#0, input_win_curr_pos(a0)
	move.w	#1, mapping_frame(a0)
	move.w	#1, routine(a0)	; => InputWindowCursor_Main
; ----------------------------------------------------------------------------
InputWindowCursor_Main:
	tst.w	(Window_queue).w
	bne.s	.display
	move.w	(Window_index).w, d0
	beq.s	.display
	move.w	(Windows_opened_num).w, d0
	subq.w	#1, d0
	lsl.w	#6, d0
	addi.w	#$E000, d0
	cmpa.w	d0, a0
	beq.s	.cont
.display:
	move.b	#$30, render_flags(a0)
	rts

.cont:
	subq.w	#1, anim_frame_timer(a0)
	bpl.s	.checkC
	move.w	#7, anim_frame_timer(a0)
	bchg	#1, render_flags(a0)
.checkC:
	btst	#Button_C, (Joypad_held).w
	beq.s	.checkdir
	rts

.checkdir:
	moveq	#0, d1
	move.b	input_win_curr_pos(a0), d1
	move.l	d1, d2
	divu.w	#input_win_char_row, d2
	move.b	(Joypad_pressed).w, d0
	lsr.b	#1, d0
	bcc.s	.checkdown		; branch if up was not pressed

; Up pressed
.up_pressed:
	subi.b	#input_win_char_row, d1
	bpl.s	.next_non_space
	addi.b	#input_win_char_row*input_win_char_col, d1
	bra.s	.next_non_space

.checkdown:
	lsr.b	#1, d0
	bcc.s	.checkleft		; branch if down was not pressed

; Down pressed
.down_pressed:
	addi.b	#input_win_char_row, d1
	cmpi.b	#input_win_char_row*input_win_char_col, d1
	bcs.s	.next_non_space
	subi.b	#input_win_char_row*input_win_char_col, d1
	bra.s	.next_non_space

.checkleft:
	lsr.b	#1, d0
	bcc.s	.checkright		; branch if left was not pressed

; Left pressed
.left_pressed:
	subq.b	#1, d1
	move.l	d1, d3
	divu.w	#input_win_char_row, d3
	cmp.w	d2, d3
	beq.s	.next_non_space
	addi.b	#input_win_char_row, d1
	bra.s	.next_non_space

.checkright:
	lsr.b	#1, d0
	bcc.w	.heldinput		; branch if right was not pressed

; Right pressed
.right_pressed:
	addq.b	#1, d1
	if revision>0
	cmpi.b	#input_win_char_row*3-2, d1
	bne.s	.s
	subi.b	#input_win_char_row-2, d1
.s:
	endif
	move.l	d1, d3
	divu.w	#input_win_char_row, d3
	cmp.w	d2, d3
	beq.s	.next_non_space_right
	subi.b	#input_win_char_row, d1

.next_non_space_right:
	lea	(InputCharacterMap).l, a1
	adda.w	d1, a1
.l:
	tst.b	(a1)
	bne.s	.update
	addq.w	#1, a1
	addq.w	#1, d1
	cmpi.b	#input_win_char_row*input_win_char_col, d1
	bcs.s	.l
	moveq	#input_win_char_row*(input_win_char_col-1), d1
	bra.s	.update

.next_non_space:
	lea	(InputCharacterMap).l, a1
	adda.w	d1, a1
.l2:
	tst.b	(a1)
	bne.s	.update
	subq.w	#1, a1
	subq.w	#1, d1
	bra.s	.l2

.update:
	move.b	d1, input_win_curr_pos(a0)
	move.w	d1, (Window_options).w
	move.w	d1, d0
	divu.w	#input_win_char_row, d1
	if revision=0
	lsl.w	#4, d1
	addi.w	#$B0, d1
	move.w	d1, y_pos(a0)
	swap	d1
	lsl.w	#3, d1
	addi.w	#$F8, d1
	move.w	d1, x_pos(a0)	
	else
	lsl.w	#3, d1
	move.w	d1, d2
	lsl.w	#1, d1
	add.w	d2, d1
	addi.w	#$B0, d1
	move.w	d1, y_pos(a0)
	swap	d1
	lsl.w	#3, d1
	addi.w	#$100, d1
	move.w	d1, x_pos(a0)	
	endif

	lea	(InputCharacterMap).l, a1
	adda.w	d0, a1
	move.w	#1, mapping_frame(a0)
	cmpi.b	#$A3, (a1)
	bcs.s	.resetheldtimer
	move.w	#2, mapping_frame(a0)
.resetheldtimer:
	move.w	#$F, input_win_held_timer(a0)
	rts

.heldinput:
	move.b	(Joypad_held).w, d0
	lsr.b	#1, d0
	bcc.s	.down_held
	subq.w	#1, input_win_held_timer(a0)
	bmi.w	.up_pressed
	rts

.down_held:
	lsr.b	#1, d0
	bcc.s	.left_held
	subq.w	#1, input_win_held_timer(a0)
	bmi.w	.down_pressed
	rts

.left_held:
	lsr.b	#1, d0
	bcc.s	.right_held
	subq.w	#1, input_win_held_timer(a0)
	bmi.w	.left_pressed
	rts

.right_held:
	lsr.b	#1, d0
	bcc.s	.resetheldtimer
	subq.w	#1, input_win_held_timer(a0)
	bmi.w	.right_pressed
	rts


