	rts
; --------------------------------------------------------------
FollowingCharacterRoutines:
	bra.w	FollowingCharacter_Init
	bra.w	FollowingCharacter_Main
	bra.w	FollowingCharacter_Return
; --------------------------------------------------------------
FollowingCharacter_Init:
	move.w	#4, $2C(a0)
	move.w	#1, $22(a0)
; --------------------------------------------------------------
FollowingCharacter_Main:
	move.l	parent_mappings(a0), mappings(a0)		; get same sprite mappings as leading character
	if walk_speed>0
	lea	(Character_pos_table).w, a2
	move.w	(Character_pos_table_index).w, d0
	moveq	#0, d1
	move.w	a0, d1
	subi.w	#$E400, d1
	if walk_speed=1
	lsr.w	#1,d1
	else
	lsr.w	#2,d1
	endif
	sub.w	d1, d0
	else
	move.l	(Characters_RAM).w, d1
	sub.l	a0, d1
	lea	(Character_pos_table).w, a2
	move.w	(Character_pos_table_index).w, d0
	add.w	d1, d0
	endif
	andi.w	#$FF, d0
	adda.w	d0, a2
	move.w	(a2)+, d0
	move.w	(a2), d1
	moveq	#3, d2
	; Fix: If not moving, don't animate
	if bugfixes=1
	moveq	#0, d3
	cmp.w	y_pos(a0), d1
	beq.s	loc_3B56
	bhi.s	loc_3B64
	move.w	#0, d2
	bra.s	loc_3B64
loc_3B56:
	moveq	#9, d2
	cmp.w	x_pos(a0), d0
	beq.s	loc_3B68
	bhi.s	loc_3B64
	move.w	#6, d2
loc_3B64:
	moveq	#1, d3
	move.w	d2, facing_dir(a0)
loc_3B68:
	move.w	d0, x_pos(a0)
	move.w	d1, y_pos(a0)
	tst.w	(Characters_RAM+x_move_steps).w
	bne.s	loc_3B7C
	tst.w	(Characters_RAM+y_move_steps).w
	beq.s	loc_3BB2
loc_3B7C:
	tst.w	(Characters_RAM+step_duration).w
	beq.s	loc_3BB0
	tst.w	d3
	beq.s	loc_3BB0	; return if position didn't change
	subq.w	#1, anim_frame_timer(a0)
	bpl.s	loc_3BB0
	else
	cmp.w	y_pos(a0), d1
	beq.s	loc_3B56
	bhi.s	loc_3B64
	move.w	#0, d2
	bra.s	loc_3B64
loc_3B56:
	moveq	#9, d2
	cmp.w	x_pos(a0), d0
	beq.s	loc_3B68
	bhi.s	loc_3B64
	move.w	#6, d2
loc_3B64:
	move.w	d2, facing_dir(a0)
loc_3B68:
	move.w	d0, x_pos(a0)
	move.w	d1, y_pos(a0)
	tst.w	(Characters_RAM+x_move_steps).w
	bne.s	loc_3B7C
	tst.w	(Characters_RAM+y_move_steps).w
	beq.s	loc_3BB2
loc_3B7C:
	tst.w	(Characters_RAM+step_duration).w
	beq.s	loc_3BB0
	subq.w	#1, anim_frame_timer(a0)
	bpl.w	loc_3BB0
	endif
	move.w	#7, anim_frame_timer(a0)
	move.w	anim_frame(a0), d0
