ReadJoypads:
	lea	(Joypad_held).w, a0	; address where joypad states are written
	lea	(HW_port_1_data).l, a1			; first joypad port
	bsr.s	JoypadRead		; first joypad
	addq.w	#2, a1			; second joypad

JoypadRead:
	move.b	#0, (a1)			; clear joypad port
	nop						; let hardware parts act
	nop
	move.b	(a1),d0			; read lower byte (00SA0000)
	lsl.b	#2, d0			; shift to get SA000000
	andi.b	#$C0, d0			; only interested in bit 6 and 7 (A and Start)
	move.b	#$40, (a1)		; second part
	nop						; delay
	nop
	move.b	(a1),d1			; read upper byte
	andi.b	#$3F, d1			; get 00CBRLDU
	or.b	d1, d0			; merge values from both registers
	not.b	d0
	move.b	d0, d1
	move.b	(a0),d2			; store previous joypad state
	eor.b	d2, d0
	move.b	d1, (a0)+		; held state in RAM
	and.b	d1, d0
	move.b	d0, (a0)+		; pressed state in RAM
	rts


VDPSetupGame:
	lea	(VDP_control_port).l, a0
	lea	(VDP_data_port).l, a1
	lea	(VDPSetupArray).l, a2
	if revision=0
	moveq	#(VDPSetupArrayEnd-VDPSetupArray)/2, d7
	else
	moveq	#(VDPSetupArrayEnd-VDPSetupArray)/2-1, d7
	endif
-
	move.w	(a2)+, (a0)
	dbf	d7, -	; set the VDP registers

	move.w	(VDPSetupArray+2).l, d0
	move.w	d0, (VDP_reg1_values).w	; save VDP reg #1 values
	moveq	#0, d0

	move.l	#$C0000000, (VDP_control_port).l	; write to CRAM
	move.w	#$3F, d7
-
	move.w	d0, (a1)
	dbf	d7, -

	move.l	#0, (V_scroll_amount).w
	move.l	#0, (H_scroll_amount).w

	lea	(VDP_control_port).l, a6

	; clear VRAM
	move.w	#$8F01, (a6)	; auto increment = 1
	move.w	#$93FF, (a6) ; DMA length (Low)
	move.w	#$94FF, (a6) ; DMA length (High)
	move.w	#$9780, (a6)
	move.w	#$4000, (a6)
	move.w	#$80, (a6)
	move.w	#0, (VDP_data_port).l
-
	move.w	(a6), d7	; read VDP status register
	andi.w	#2, d7	; DMA busy
	bne.b	-	; loop until DMA is done
	move.w	#$8F02, (a6)	; auto increment = 2
	rts

