; -----------------------------------------------------------------
; Copies a plane map to a plane table
; Inputs:
; a1 = map address
; d0 = VDP command to write to the plane table
; d1 = number of tiles per row
; d2 = columns
; -----------------------------------------------------------------
PlaneMapToVRAM:
	lea	(VDP_control_port).l, a2
	lea	(VDP_data_port).l, a3
	move.l	#$800000, d7	; row increment value
-
	move.l	d0, (a2)	; write to control port to draw art
	move.w	d1, d4
-
	move.w	(a1)+, (a3)
	dbf	d4, -	; copy one row
	add.l	d7, d0	; move onto next row
	dbf	d2, --	; and copy it
	rts
; -----------------------------------------------------------------


; -----------------------------------------------------------------
DecompressToVDP:
	lea	(Decom_chunk_buffer).w, a3
	moveq	#0, d0
	moveq	#0, d2
	move.b	(a0)+, d2	; get number of definitions
	beq.w	.inline
	bmi.w	.end
	subq.w	#1, d2
.reploop:
	lea	(Decom_chunk_buffer).w, a2
	movea.l	a2, a3
	move.b	(a0)+, d3	; get byte to copy
	move.b	(a0)+, d4
	lsl.w	#8, d4
	move.b	(a0)+, d4
	lsl.l	#8, d4
	move.b	(a0)+, d4
	lsl.l	#8, d4
	move.b	(a0)+, d4	; d4 = get bit counts for current byte to copy
	or.l	d4, d0		; keep track of number of bytes copied

	moveq	#$1F, d7
.copybyteloop:
	lsl.l	#1, d4
	bcc.w	.nextbyte	; if not set, skip
	move.b	d3, (a2)	; copy byte
.nextbyte:
	addq.w	#1, a2
	dbf	d7, .copybyteloop

	dbf	d2, .reploop

	moveq	#-1, d1
	eor.l	d0, d1		; check how many bits are 0
	beq.w	.write		; if we copied all 32 bytes, write data

; write remaining bytes
.inline:
	moveq	#$1F, d7
.inlineloop:
	lsl.l	#1, d0
	bcs.w	.nextinline
	move.b	(a0)+, (a3)
.nextinline:
	addq.w	#1, a3
	dbf	d7, .inlineloop

.write:
	lea	(Decom_chunk_buffer).w, a6
	lea	(VDP_data_port).l, a5
	rept 8
	move.l	(a6)+, (a5)
	endm
	bra.s	DecompressToVDP	; process next 32 bytes

.end:
	rts
; -----------------------------------------------------------------


; -----------------------------------------------------------------
; Same as above, but it decompresses to RAM instead
; -----------------------------------------------------------------
DecompressToRAM:
	lea	(Decom_chunk_buffer).w, a3
	moveq	#0, d0
	moveq	#0, d2
	move.b	(a0)+, d2
	beq.w	.inline
	bmi.w	.end
	subq.w	#1, d2

.reploop:
	lea	(Decom_chunk_buffer).w, a2
	movea.l	a2, a3
	move.b	(a0)+, d3
	move.b	(a0)+, d4
	lsl.w	#8, d4
	move.b	(a0)+, d4
	lsl.l	#8, d4
	move.b	(a0)+, d4
	lsl.l	#8, d4
	move.b	(a0)+, d4
	or.l	d4, d0

	moveq	#$1F, d7
.copybyteloop:
	lsl.l	#1, d4
	bcc.w	.nextbyte
	move.b	d3, (a2)
.nextbyte:
	addq.l	#1, a2
	dbf	d7, .copybyteloop

	dbf	d2, .reploop

	moveq	#-1, d1
	eor.l	d0, d1
	beq.w	.write

.inline:
	moveq	#$1F, d7
.inlineloop:
	lsl.l	#1, d0
	bcs.w	.nextinline
	move.b	(a0)+, (a3)
.nextinline:
	addq.l	#1, a3
	dbf	d7, .inlineloop

.write:
	lea	(Decom_chunk_buffer).w, a6
	rept 8
	move.l	(a6)+, (a4)+
	endm
	bra.s	DecompressToRAM

.end:
	rts
; -----------------------------------------------------------------


; -----------------------------------------------------------------
RunDecompressionQueue:
	tst.l	(Decom_queue).w
	beq.w	.return
	move.b	(Decom_queue_unknown).w, d0
	bne.w	.return
	lea	(Decom_buffer).w, a1
	move.b	#0, (Decom_queue_chunk_num).w
	movea.l	(Decom_queue).w, a0
	move.w	#$F, d6
.chunkloop:
	lea	(Decom_chunk_buffer).w, a3
	moveq	#0, d0
	moveq	#0, d2
	move.b	(a0)+, d2
	beq.w	.inline
	bmi.w	.nextflag
	subq.w	#1, d2
