RenderWindows:
	move.w	(Window_column_frames_left).w, d1
	bne.w	RenderWindows_Cont
	move.w	(Window_queue).w, d0
	beq.w	Win_CheckRenderScript	; when all windows are loaded, render script
	bmi.w	DestroyWindows			; if bit 7 is set, destroy window

; Create window
	andi.w	#$FF, d0
	lea	(WindowArtLayoutPtrs-8).l, a1
	lsl.w	#3, d0
	adda.w	d0, a1
	move.w	(Camera_Y_pos_BG).w, d0
	andi.w	#$F, d0
	bne.w	RenderWindows_Return	; wait until camera has updated 16 pixels
	move.w	(Camera_X_pos_BG).w, d0
	andi.w	#$F, d0
	bne.w	RenderWindows_Return
	lea	(Window_draw_cache).w, a0
	move.w	(Windows_opened_num).w, d0
	lsl.w	#4, d0
	adda.w	d0, a0
	lea	(Camera_Y_pos_FG).w, a5
	move.w	(a5)+, d4	; d4 = Camera Y
	move.w	(a5), d5	; d5 = Camera X
	andi.w	#$F8, d4
	andi.w	#$1F8, d5
	lsl.w	#4, d4		; calculate VDP address
	lsr.w	#2, d5
	add.w	d5, d4
	move.w	d4, d0		; d0 = plane address
	move.w	(a1), d1	; plane offset
	andi.w	#$FF80, d1	; get row
	add.w	d1, d0
	move.w	(a1)+, d1
	andi.w	#$7F, d1	; get cell
	move.w	d0, d2
	add.w	d1, d0
	eor.w	d0, d2
	andi.w	#$80, d2	; find the difference between final address and row offset
	beq.s	+			; branch if we're between 0 and $7F
	subi.w	#$80, d0	; rollover
+
	andi.w	#$EFFF, d0	; value between 0 and $FFF
	move.w	d0, (a0)+	; store VDP address
	move.l	(Win_backup_tiles_addr).w, (a0)+
	move.l	(a1)+, (a0)+	; store plane mappings address
	moveq	#0, d0
	move.b	(a1)+, d0
	move.w	d0, (a0)+	; store number of columns
	move.w	d0, (Window_total_columns).w
	move.w	d0, d1
	lsr.w	#1, d0
	addq.w	#1, d0
	move.w	d0, (Window_column_frames_left).w
	andi.w	#1, d1
	move.w	d1, (Window_column_frames_num).w
	btst	#1, (Window_queue).w
	beq.s	+			; branch if window opens in multiple frames
	move.w	(Window_total_columns).w, (Window_column_frames_num).w	; otherwise pop open
	move.w	#1, (Window_column_frames_left).w
+
	move.b	(a1)+, d0
	move.w	d0, (a0)+	; store number of rows
	move.w	d0, (Window_total_rows).w
	move.w	(Window_queue).w, d0
	andi.w	#$FF, d0
	move.w	d0, (a0)	; store window index
	btst	#2, (Window_queue).w
	bne.s	+		; branch if it's a static window (don't save it in RAM)
	lea	(Window_draw_cache).w, a0
	move.w	(Windows_opened_num).w, d0
	lsl.w	#4, d0
	adda.w	d0, a0
	move.w	(a0)+, d0	; VDP address
	andi.w	#$2FFF, d0
	addq.w	#8, a0
	move.w	(a0)+, d1	; get columns
	move.w	(a0)+, d2	; get rows
	movea.l	(Win_backup_tiles_addr).w, a1
	bsr.w	Win_BackupTiles	; save the tiles that will be covered by the window
	move.l	a1, (Win_backup_tiles_addr).w
+
	btst	#1, (Window_queue).w
	beq.s	RenderWindows_Cont
RenderWindows_Return:
	rts

RenderWindows_Cont:
	move.w	(Window_queue).w, d0
	bmi.w	DestroyWindows_Cont	; branch if set to destroy
	lea	(Window_draw_cache).w, a0
	move.w	(Windows_opened_num).w, d0
	lsl.w	#4, d0
	adda.w	d0, a0
	move.w	(a0)+, d0	; d0 = VDP address
	addq.w	#4, a0		; skip backup tiles address
	movea.l	(a0)+, a1	; a1 = plane mappings address
	subq.w	#1, d1
	adda.w	d1, a1
	move.w	(a0)+, d3	; get columns
	move.w	(a0)+, d2	; get rows
	move.w	(Window_column_frames_num).w, d1
	bsr.w	Win_Draw
	addq.w	#2, (Window_column_frames_num).w
	subq.w	#1, (Window_column_frames_left).w
	bne.s	+
	btst	#2, (Window_queue).w
	bne.s	ShiftUpWindowQueue
	addq.w	#1, (Windows_opened_num).w
	andi.w	#$F, (Windows_opened_num).w
	bne.s	ShiftUpWindowQueue
	movea.l	#RAM_start&$FFFFFF, a0
	move.l	a0, (Win_backup_tiles_addr).w
ShiftUpWindowQueue:
	move.l	(Window_queue+2).w, d0
	move.l	d0, (Window_queue).w
	move.l	(Window_queue+6).w, d0
	move.l	d0, (Window_queue+4).w
	move.l	(Window_queue+$A).w, d0
	move.l	d0, (Window_queue+8).w
	move.l	(Window_queue+$E).w, d0
	move.l	d0, (Window_queue+$C).w
+
	rts


