	move.w	#3, (a2)	; plane address
	move.w	#$8500, d2
	move.b	(a4)+, d2
	move.w	d2, (a3)
	addi.w	#$80, d0	; skip line
	andi.w	#$EFFF, d0
	move.w	d0, (a2)
	move.w	#3, (a2)
	move.b	(a4), d2
	move.w	d2, (a3)
	move.w	(Text_plane_offset).w, d0
	move.b	d0, d1
	andi.b	#$80, d1
	addq.b	#2, d0
	andi.b	#$7F, d0
	or.b	d1, d0
	andi.w	#$EFFF, d0
	move.w	d0, (Text_plane_offset).w
	addq.w	#1, a1
	rts
; -----------------------------------------------------------------


; -----------------------------------------------------------------
Win_ShiftUpText:
	move.w	(Text_plane_offset_start).w, d0
	addi.w	#$80, d0
	andi.w	#$FFF, d0
	move.w	#$17, d1
	move.w	(Text_max_line_num).w, d2
	beq.s	Win_Clear
	movea.l	(Win_backup_tiles_addr).w, a1
	bsr.w	Win_BackupTiles	; back up text
	move.w	(Text_plane_offset_start).w, d0
	andi.w	#$CFFF, d0
	move.w	#$17, d1
	move.w	(Text_max_line_num).w, d2
	movea.l	(Win_backup_tiles_addr).w, a1
	bsr.w	Win_TilesToVDP
	move.w	#$17, d1
-
	bsr.w	Win_VDPAddressToControlPort
	move.w	#$8526, (a3)
	dbf	d1, -
	rts
; -----------------------------------------------------------------


; -----------------------------------------------------------------
Win_Clear:
	move.w	(Text_plane_offset_start).w, d0
	andi.w	#$CFFF, d0
	move.w	#$17, d1
	move.w	(Text_max_line_num).w, d2
	bne.s	+
; for the window in battle
	if revision=0
	move.w	#$11, d1
	else
	move.w	#$13, d1
	endif
+
	addq.w	#1, d2	; include last empty line
	move.l	#$80, d7
-
	move.w	d1, d4
	move.w	d0, d5
-
	bsr.w	Win_VDPAddressToControlPort
	move.w	#$8526, (a3)
	dbf	d4, -
	move.w	d5, d0
	add.l	d7, d0
	dbf	d2, --
	move.w	#0, (Text_curr_line).w
	rts
; -----------------------------------------------------------------


; -----------------------------------------------------------------
; Refresh the Agility, Attack and Defense parameters
Win_RefreshStatsNum:
	tst.w	(Refresh_stats_flag).w
	bne.s	+
	rts

+
	move.w	#0, (Refresh_stats_flag).w
	lea	(VDP_control_port).l, a2
	lea	(VDP_data_port).l, a3
	lea	(Win_agility_num).w, a0
	move.w	#$112, d1
	bsr.s	+
	lea	(Win_attack_num).w, a0
	move.w	#$212, d1
	bsr.s	+
	lea	(Win_defense_num).w, a0
	move.w	#$312, d1
+
	move.w	(a0)+, d5
	cmp.w	(a0), d5
	beq.s	++
	bcs.s	+
	addq.w	#1, (a0)
	bra.s	++
+
	subq.w	#1, (a0)
+
	move.w	(a0), d5
	move.w	(Window_draw_cache+$40).w, d0	; get VDP offset
	move.w	d0, d2
	add.w	d1, d0
	eor.w	d0, d2
	andi.w	#$80, d2
	beq.s	+
	subi.w	#$80, d0
+
	lea	(DecimalDigitData+$14).l, a1
	moveq	#2, d1
	moveq	#0, d4
-
	bsr.w	Win_VDPAddressToControlPort
	tst.b	d1
	bne.s	+
	move.b	#1, d4
+
	move.w	#$8597, d2
	move.l	(a1)+, d3
-
	sub.w	d3, d5
	bcs.s	+
	addq.b	#1, d2
	bra.s	-
+
	add.w	d3, d5
	tst.b	d4
	bne.s	+
	cmpi.w	#$8597, d2
	beq.s	++
	move.b	#1, d4
+
	move.w	d2, (a3)
	dbf	d1, --
	rts
+
	move.w	#$8526, (a3)
	dbf	d1, --
	rts
; -----------------------------------------------------------------


; -----------------------------------------------------------------
; d0 = VDP address
; d1 = column frames number
; d2 = rows
; d3 = columns
; a1 = plane mappings
; -----------------------------------------------------------------
Win_Draw:
	lea	(VDP_control_port).l, a2
	lea	(VDP_data_port).l, a3
	move.l	#$80, d7	; row increment
	move.w	d3, d5
	sub.w	d1, d5
	andi.w	#$FFFE, d5
	move.b	d0, d6
	andi.b	#$80, d6
	add.b	d5, d0	; add offset to VDP address
	andi.b	#$7F, d0
	or.b	d6, d0
	subq.w	#2, d2	; don't account for top and bottom border
	sub.w	d1, d3	; offset from center
	move.w	#$8500, d5	; d5 = starting plane tile
	btst	#$D, d0
	beq.s	+	; if we're writing to plane A, branch (priority bit set)
	move.w	#$500, d5	; if we're writing to plane B, clear priority bit
+
	move.l	d0, -(sp)
	move.w	d1, d4
	bsr.w	Win_VDPAddressToControlPort
	move.b	#$B8, d5	; top left corner
	move.w	d5, (a3)
	dbf	d4, Win_DrawTopNextTile
	bra.s	Win_DrawMiddle

Win_DrawTopBorderLoop:
	move.b	(a1)+, d5
	bsr.w	Win_VDPAddressToControlPort
	move.w	d5, (a3)
Win_DrawTopNextTile:
	dbf	d4, Win_DrawTopBorderLoop
	bsr.w	Win_VDPAddressToControlPort
	move.b	#$BA, d5	; top right corner
	move.w	d5, (a3)
Win_DrawMiddle:
	move.l	(sp)+, d0
	adda.w	d3, a1	; next plane mappings row
	add.w	d7, d0	; next VDP row
Win_DrawMiddleLoop:
	move.l	d0, -(sp)
	move.w	d1, d4
	bsr.w	Win_VDPAddressToControlPort
	move.b	#$BB, d5	; left border
	move.w	d5, (a3)
	dbf	d4, Win_DrawMiddleNextTile
	bra.s	Win_DrawMiddleNextRow

Win_DrawMiddleTileLoop:
	bsr.w	Win_VDPAddressToControlPort
	move.w	(Window_art_tile_start).w, d6
	move.b	(a1)+, d6
	move.w	d6, (a3)
Win_DrawMiddleNextTile:
	dbf	d4, Win_DrawMiddleTileLoop
	bsr.w	Win_VDPAddressToControlPort
	move.b	#$BC, d5	; right border
	move.w	d5, (a3)

Win_DrawMiddleNextRow:
	move.l	(sp)+, d0
	adda.w	d3, a1
	add.w	d7, d0
	dbf	d2, Win_DrawMiddleLoop
	move.w	d1, d4
	bsr.w	Win_VDPAddressToControlPort
	move.b	#$BD, d5	; bottom left corner
	move.w	d5, (a3)
	dbf	d4, Win_DrawBottomNextTile
	rts
Win_DrawBottomBorderLoop:
	move.b	(a1)+, d5
	bsr.w	Win_VDPAddressToControlPort
	move.w	d5, (a3)
Win_DrawBottomNextTile:
	dbf	d4, Win_DrawBottomBorderLoop
	bsr.w	Win_VDPAddressToControlPort
	move.b	#$BF, d5	; bottom right corner
	move.w	d5, (a3)
	rts
; -----------------------------------------------------------------


; -----------------------------------------------------------------
; d0 = VDP address
; a2 = VDP control port
;
; Returns
;
; d0 = VDP address + 2
; -----------------------------------------------------------------
Win_VDPAddressToControlPort:
	andi.w	#$EFFF, d0
	move.w	d0, (a2)
	move.w	#3, (a2)	; Plane address
	move.b	d0, d6
	andi.b	#$80, d6
	addq.b	#2, d0
	andi.b	#$7F, d0
	or.b	d6, d0
	rts
; -----------------------------------------------------------------


; -----------------------------------------------------------------
; d0 = VDP address
; d1 = column frames number
; d2 = rows
; d3 = columns
; a1 = backup tiles
; -----------------------------------------------------------------
Win_Erase:
	lea	(VDP_control_port).l, a2
	lea	(VDP_data_port).l, a3
	move.l	#$80, d7	; row increment
	movea.l	a1, a4
	add.w	d3, d3	; multiply by 2 (2 bytes per plane pattern)
	cmpi.w	#1, (Window_column_frames_left).w
	bne.w	Win_EraseShrinkWindow

; for the last frame, window disappears completely
	andi.w	#$FFFE, d1
	move.b	d0, d6
	andi.b	#$80, d6
	add.b	d1, d0
	andi.b	#$7F, d0
	or.b	d6, d0
	adda.w	d1, a1
	move.w	d2, d4
Win_EraseLastLoop:
	move.w	d0, d5
	bsr.w	Win_VDPAddressToControlPort
	move.w	(a1)+, (a3)
	bsr.w	Win_VDPAddressToControlPort
	move.w	(a1), (a3)
	move.w	d5, d0
	adda.w	d3, a1
	add.w	d7, d0
	dbf	d4, Win_EraseLastLoop

	rts

Win_EraseShrinkWindow:
	move.l	d0, -(sp)
	move.w	d3, d5
	sub.w	d1, d5
	andi.w	#$FFFE, d5
	adda.w	d5, a1
	subq.w	#2, d5
	move.b	d0, d6
	andi.b	#$80, d6
	add.b	d5, d0
	andi.b	#$7F, d0
	or.b	d6, d0
	subq.w	#2, d2	; don't account for top and bottom border
	move.w	d2, d4
	move.w	#$8500, d5
	move.l	d0, -(sp)
	bsr.w	Win_VDPAddressToControlPort
	move.w	#$85BA, (a3)	; draw top right corner
	bsr.w	Win_VDPAddressToControlPort
	move.w	(a1)+, (a3)		; then draw tile
	move.l	(sp)+, d0
	adda.w	d3, a1
	add.w	d7, d0
Win_EraseRightLoop:
	move.l	d0, -(sp)
	bsr.w	Win_VDPAddressToControlPort
	move.w	#$85BC, (a3)	; draw right border
	bsr.w	Win_VDPAddressToControlPort
	move.w	(a1)+, (a3)
	move.l	(sp)+, d0
	adda.w	d3, a1	; next row
	add.w	d7, d0
	dbf	d4, Win_EraseRightLoop

	bsr.w	Win_VDPAddressToControlPort
	move.w	#$85BF, (a3)	; draw bottom right corner
	bsr.w	Win_VDPAddressToControlPort
	move.w	(a1), (a3)

; now draw left side
	move.l	(sp)+, d0
	movea.l	a4, a1
	andi.w	#$FFFE, d1
	move.b	d0, d6
	andi.b	#$80, d6
	add.b	d1, d0
	andi.b	#$7F, d0
	or.b	d6, d0
	adda.w	d1, a1
	move.w	d2, d4	; rows
	move.l	d0, -(sp)
	bsr.w	Win_VDPAddressToControlPort
	move.w	(a1)+, (a3)
	bsr.w	Win_VDPAddressToControlPort
	move.w	#$85B8, (a3)	; draw top left corner
	move.l	(sp)+, d0
	adda.w	d3, a1	; next row
	add.w	d7, d0
Win_EraseLeftLoop:
	move.l	d0, -(sp)
	bsr.w	Win_VDPAddressToControlPort
	move.w	(a1)+, (a3)
	bsr.w	Win_VDPAddressToControlPort
	move.w	#$85BB, (a3)	; draw left border
	move.l	(sp)+, d0
	adda.w	d3, a1	; next row
	add.w	d7, d0
	dbf	d4, Win_EraseLeftLoop

	bsr.w	Win_VDPAddressToControlPort
	move.w	(a1), (a3)
	bsr.w	Win_VDPAddressToControlPort
	move.w	#$85BD, (a3)	; draw bottom left corner
	rts
; -----------------------------------------------------------------


; -----------------------------------------------------------------
; d0 = VDP address
; d1 = columns
; d2 = rows
; a1 = tile address
; -----------------------------------------------------------------
Win_TilesToVDP:
	lea	(VDP_control_port).l, a2
	lea	(VDP_data_port).l, a3
	move.l	#$80, d7
-
	move.w	d1, d4
	move.w	d0, d5
-
	bsr.w	Win_VDPAddressToControlPort
	move.w	(a1)+, (a3)
	dbf	d4, -

	move.w	d5, d0
	add.l	d7, d0
	dbf	d2, --
	rts
; -----------------------------------------------------------------


; -----------------------------------------------------------------
; d0 = VDP address
; d1 = columns
; d2 = rows
; a1 = backup tiles address
; -----------------------------------------------------------------
Win_BackupTiles:
	lea	(VDP_control_port).l, a2
	lea	(VDP_data_port).l, a3
	move.l	#$80, d7
-
	move.w	d1, d4
	move.w	d0, d5
-
	bsr.w	Win_VDPAddressToControlPort
	move.w	(a3), (a1)+	; read tile from VDP
	dbf	d4, -

	move.w	d5, d0
	add.l	d7, d0	; next row
	dbf	d2, --

	rts
; -----------------------------------------------------------------


OpenMenuWindow:
	tst.w	(Interaction_routine).w
	bne.s	+	; rts
	tst.w	(Window_queue).w
	bne.s	+	; rts
	tst.w	(Window_index).w
	bne.s	+	; rts
	tst.w	(Script_flag).w
	bne.s	+	; rts
	move.w	(Window_routine_2).w, d1
	bne.s	loc_9A20
	tst.w	(Cutscene_flag).w
	bne.s	+	; rts
	tst.b	(Map_event_load).w
	bne.s	+	; rts

	move.b	(Joypad_pressed).w, d0
	btst	#Button_C, d0		; C button pressed
	beq.s	+	; if not, return
	move.b	#SFXID_Selection, (Sound_queue).w
	move.w	#WinID_PlayerMenu, (Window_queue).w
	move.w	#1, (Window_routine_2).w
	move.w	#0, (Window_routine_3).w
+
	rts

loc_9A20:
	move.w	($FFFFDE80).w, d0
	lsl.w	#2, d0
	andi.w	#$1C, d0
	jmp	Menu_FirstWindowEntries(pc,d0.w)

; -------------------------------------------
Menu_FirstWindowEntries:
	bra.w	Menu_ItemEntry
	bra.w	Menu_StateEntry
	bra.w	Menu_TechEntry
	bra.w	Menu_StrngEntry
	bra.w	Menu_EqpEntry
; -------------------------------------------

Menu_ItemEntry:
	lsl.w	#2, d1
	andi.w	#$1C, d1
	jmp	Menu_Item_RoutineIndex-4(pc,d1.w)

; ------------------------------------------------
Menu_Item_RoutineIndex:
	bra.w	Menu_Item_RoutineOpened
	bra.w	Menu_Item_RoutineCharSelected
	bra.w	Menu_Item_RoutineItemWindowOpened
	bra.w	Menu_Item_RoutineItemActionEntries
; ------------------------------------------------

Menu_Item_RoutineOpened:
	move.w	#WinID_MenuItemChar, (Window_queue).w
	addq.w	#1, (Window_routine_2).w
	rts

; -------------------------------------------

Menu_Item_RoutineCharSelected:
	move.w	(Window_routine_3).w, d1
	bne.w	Win_CloseCurrent
	lea	(Character_stats+item_num).w, a2			; get character's number of items
	move.w	(Character_index).w, d1
	lsl.w	#6, d1
	adda.w	d1, a2		; get character selected
	tst.b	(a2)
	bne.s	loc_9A92					; branch if character is carrying at least one item
	move.w	#$23, (Script_queue).w		; "isn't carrying anything" text
	move.w	#WinID_ScriptMessage, (Window_queue).w
	addq.w	#1, (Window_routine_3).w
	rts

loc_9A92:
	move.w	#WinID_MenuItemList, (Window_queue).w
	addq.w	#1, (Window_routine_2).w
	rts

; -------------------------------------------

Menu_Item_RoutineItemWindowOpened:
	move.w	#WinID_ItemAction, (Window_queue).w
	addq.w	#1, (Window_routine_2).w
	rts

; -------------------------------------------

Menu_Item_RoutineItemActionEntries:
	move.w	($FFFFDE88).w, d0
	lsl.w	#2, d0
	andi.w	#$C, d0
	jmp	ItemActionEntries(pc,d0.w)

; -------------------------------------------
ItemActionEntries:
	bra.w	ItemAction_UseEntry
	bra.w	ItemAction_GiveEntry
	bra.w	ItemAction_TossEntry
; -------------------------------------------

ItemAction_UseEntry:
	move.w	(Window_routine_3).w, d1
	bne.s	+
	move.w	#WinID_ScriptMessage, (Window_queue).w
	addq.w	#1, (Window_routine_3).w
+
	lea	(Character_stats+curr_hp).w, a2		; character's current HP
	move.w	(Character_index).w, d0
	lsl.w	#6, d0
	adda.w	d0, a2		; get selected character
	tst.w	(a2)
	bne.s	++				; branch if character is not dead
	cmpi.w	#1, (Window_routine_3).w
	bne.s	+
	move.w	#6, (Script_queue).w
	addq.w	#1, (Window_routine_3).w
	rts

+
	subq.w	#1, (Window_routine_3).w
	bra.w	Win_CloseCurrent

+
	moveq	#0, d0
	move.b	(Item_index).w, d0
	move.w	d0, d2
	lsl.w	#4, d2
	lea	(InventoryData+$C).l, a0		; determine kind of item
	adda.w	d2, a0
	btst	#3, (a0)
	beq.s	ItemAction_NoAction		; if the bit is not set, do nothing for this item
	cmpi.w	#ItemID_NeiSword, d0
	beq.w	NeiSword_ItemSelected
	cmpi.w	#ItemID_Teim, d0
	beq.w	Teim_ItemSelected
	cmpi.w	#ItemID_Visiphone, d0
	beq.w	Visiphone_ItemSelected

	lsl.w	#2, d0
	andi.w	#$1FC, d0
	jmp	UsedItemActionIndex(pc,d0.w)

; -------------------------------------------
UsedItemActionIndex:
	bra.w	ItemAction_NoAction
	bra.w	ItemAction_SmallKey
	bra.w	ItemAction_Dynamite
	bra.w	ItemAction_KeyTube
	bra.w	ItemAction_MruraGum
	bra.w	ItemAction_GreenCard
	bra.w	ItemAction_BlueCard
	bra.w	ItemAction_YellowCard
	bra.w	ItemAction_RedCard
	bra.w	ItemAction_Letter
	bra.w	ItemAction_Recorder
	bra.w	ItemAction_MruraLeaf
	bra.w	ItemAction_PlsmRing
	bra.w	ItemAction_Prism
	bra.w	ItemAction_Telepipe
	bra.w	ItemAction_Escapipe
	bra.w	ItemAction_Hidapipe
	bra.w	ItemAction_Monomate
	bra.w	ItemAction_Monomate
	bra.w	ItemAction_Monomate
	bra.w	ItemAction_Antidote
	bra.w	ItemAction_StarMist
	bra.w	ItemAction_MoonDew
; -------------------------------------------
ItemAction_NoAction:
	tst.w	d1
	bne.s	+
	move.w	#1, (Script_queue).w		; "'Character' uses 'Item'...."
	rts

+
	subq.w	#1, d1
	bne.w	CloseAllWindows
	move.w	#4, (Script_queue).w		; "Nothing happens."
	addq.w	#1, (Window_routine_3).w
	rts
; -------------------------------------------
ItemAction_SmallKey:
	tst.w	d1
	bne.s	loc_9BEC
	bsr.w	CheckItemInteract
	beq.s	loc_9BE0	; we didn't interact with anything (next to no object) so display item description
	cmpi.b	#2, $2E(a1)
	bne.s	loc_9BE0
	moveq	#0, d1
	move.b	$2F(a1), d1
	lea	(Treasure_chest_flags).w, a1
	adda.w	d1, a1
	bclr	#7, (a1)					; mark chest as unlocked
	beq.s	loc_9BE0					; if it's already unlocked, branch
	move.w	#1, (Script_queue).w		; "'Character' uses 'Item'...."
	rts

loc_9BE0:
	move.w	#$15, (Script_queue).w		; "It is a key for a small container."
	addq.w	#1, (Window_routine_3).w
	rts

loc_9BEC:
	subq.w	#1, d1
	bne.w	CloseAllWindows
	move.w	#$17, (Script_queue).w		; "The container opens."
	addq.w	#1, (Window_routine_3).w
	rts


; -----------------------------------------------------------------
CheckItemInteract:
	lea	(Characters_RAM).w, a0
	move.w	facing_dir(a0), d6
	beq.s	+
	divu.w	#3, d6
+
	bsr.w	Map_ChkTargetInteract
	rts
; -----------------------------------------------------------------


; -----------------------------------------------------------------
; Returns
;
; d1 = event flag offset; 0 if item is not found
; a1 = event flag address
; -----------------------------------------------------------------
GetItemEventFlag:
	bsr.w	CheckItemInteract
	beq.s	+
	tst.b	type(a1)
	bne.s	+
	moveq	#0, d1
	move.b	subtype(a1), d1
	lea	(Event_flags).w, a1
	adda.w	d1, a1
	rts
+
	moveq	#0, d1
	rts
; -----------------------------------------------------------------


; -----------------------------------------------------------------
ItemAction_Dynamite:
	tst.w	d1
	bne.w	CloseAllWindows
	bsr.s	GetItemEventFlag
	beq.s	.nouse
	cmpi.b	#$18, d1
	beq.s	.use
	cmpi.b	#$19, d1
	beq.s	.use
	cmpi.b	#$1A, d1
	bne.s	.nouse
.use:
	bset	#0, (a1)
	bne.s	.nouse
	move.w	#1, (Script_queue).w		; "'Character' uses 'Item'...."
	move.w	#1, (Cutscene_flag).w
	move.w	#4, (Cutscene_index).w
	move.w	#0, (Cutscene_input_frame).w
	bra.w	RemoveItemFromInventory

.nouse:
	move.w	#$14, (Script_queue).w		; "'Character' takes out 'Item' and puts it back."
	rts
; -----------------------------------------------------------------


; -----------------------------------------------------------------
ItemAction_KeyTube:
	tst.w	d1
	bne.w	CloseAllWindows
	bsr.w	GetItemEventFlag
	beq.s	.nouse
	cmpi.b	#$1B, d1
	bne.s	.nouse
	bset	#0, (a1)
	bne.s	.nouse
	move.w	#$18, (Script_queue).w		; "'Character' puts 'Item' inside."
