FindInventoryItem:
	lea	(Party_member_ID).w, a1
	move.w	(Party_members_num).w, d1
-
	move.w	(a1)+, d0
	move.w	d0, (Character_index).w
	lea	(Character_stats+items).w, a2
	lsl.w	#6, d0
