Interact_TreasureChest:
	tst.w	(Interaction_routine_2).w
	bne.w	CloseAllWindows
	moveq	#0, d1
	move.b	(Interaction_subtype).w, d1
	beq.s	TreasureChest_Empty
	lea	(Treasure_chest_flags).w, a0
	adda.w	d1, a0
	lea	(TreasureChestContentArray-2).l, a1
	add.w	d1, d1
	adda.w	d1, a1
	tst.b	(a0)
	beq.s	loc_E804
	bmi.s	TreasureChest_Locked

TreasureChest_Empty:
	move.w	#$1706, (Script_queue).w		; "There is nothing inside."
	addq.w	#1, (Interaction_routine_2).w
	rts

TreasureChest_Locked:
	tst.w	(a1)
	beq.w	loc_E8B4
	move.w	#$1707, (Script_queue).w		; "It's locked and won't open."
	addq.w	#1, (Interaction_routine_2).w
	rts

loc_E804:
	move.w	(a1), d0
	bmi.s	Interact_AddItem
	beq.w	loc_E8B4
	ext.l	d0
	move.l	d0, (Meseta_value).w
	bsr.w	AddToCurrentMoney
	move.b	#SFXID_ItemReceived, (Sound_queue).w
	move.b	#1, (a0)
	move.w	#$1701, (Script_queue).w
	addq.w	#1, (Interaction_routine_2).w
	lea	(Character_stats).w, a2
	lea	(Party_member_ID).w, a1
	move.w	(Party_members_num).w, d1
-
	move.w	(a1)+, d0
	move.w	d0, (Character_index).w
	lsl.w	#6, d0
	tst.w	2(a2,d0.w)
	bne.s	+			; return if character is not dead (display the name of the one, not dead, who found the money)
	dbf	d1, -			; loop until we find a character who's not dead

+
	rts

Interact_AddItem:
	move.b	d0, (Item_index).w
	move.w	#$1702, (Script_queue).w		; "'CHARACTER' has found 'ITEM'."
	lea	(Party_member_ID).w, a1
	move.w	(Party_members_num).w, d1
loc_E85C:
	move.w	(a1)+, d0
	move.w	d0, (Character_index).w
	lea	(Character_stats+items).w, a2
	lsl.w	#6, d0
	adda.w	d0, a2
	tst.w	-$26(a2)
	beq.s	loc_E884			; branch if character's dead
	move.w	#0, (Inventory_offset).w
	moveq	#$F, d0
loc_E878:
	tst.b	(a2)+
	beq.s	loc_E896
	addq.w	#1, (Inventory_offset).w
	dbf	d0, loc_E878

loc_E884:
	dbf	d1, loc_E85C
	move.w	#$1704, ($FFFFCD02).w
	addq.w	#1, (Interaction_routine_2).w
	moveq	#-1, d0
	rts
loc_E896:
	move.w	#$1703, ($FFFFCD02).w
	addq.b	#1, (a0)
	move.w	(Character_index).w, d1
	bsr.w	AddItemToInventory2
	move.b	#SFXID_ItemReceived, (Sound_queue).w
	addq.w	#1, (Interaction_routine_2).w
	moveq	#0, d0
	rts

loc_E8B4:
	move.b	#1, (a0)
	move.w	#$1705, (Script_queue).w		; "It's full of garbage."
	addq.w	#1, (Interaction_routine_2).w
	rts

