	subq.w	#1, d3
	bpl.s	+
	move.b	#2, render_flags(a0)	; sprite not visible
	rts
+
	move.b	#0, render_flags(a0)
	lea	(Character_stats+curr_hp).w, a1
	move.w	(a2)+, d0
	move.w	d0, d2
	lsl.w	#6, d2
	adda.w	d2, a1
	tst.w	(a1)
