; -----------------------------------------------------------------
; Sound Driver
; -----------------------------------------------------------------

SoundDriverBase:

; function to make a little-endian 16-bit pointer for the z80
z80ptr	function addr, ((addr-SoundDriverBase)<<8)&$FF00|(addr-SoundDriverBase)>>8
	
;	if revision=0               
; 'soundrev' can overrule 'revision' if non-zero. If zero, revision is used.
	switch soundrev
	case 0
	if revision=0
    include "sound/ps2.sound_driver_jap.asm"
	else
	include "sound/ps2.sound_driver.asm"
    endif                       ;if revision=0
	case 1,2                      ;if soundrev=1, use Japanese release sound engine; if soundrev=2, use JP sound engine with quieter pcm drums below
	include "sound/ps2.sound_driver_jap.asm"
	elsecase                    ;if soundrev is anything else (3, for instance), use US release sound engine
	include "sound/ps2.sound_driver.asm"
	endcase

; loc_BDC2A:
PCMDrums:
	!org 0		; we need to start from an address less than $10000 for Z80 otherwise the assembler throws an Address Overflow error; so we conveniently start from 0
	save		; save 68000 CPU settings
	switch soundrev
	case 0
	if revision=0
	include "sound/ps2.pcm_drums_jap.asm"
	else
	include "sound/ps2.pcm_drums.asm"
	endif
	case 1
	include "sound/ps2.pcm_drums_jap.asm" ; if soundrev=1, use Japanese release's PCM drum samples
	case 2
    ;; NOTE: I think the _quiet.asm version needs redoing; I think I didn't do something right.
	include "sound/ps2.pcm_drums_jap_quiet.asm" ; if soundrev=2, use Japanese release PCM drum samples, except with a lower volume 'snare drum' sample.
	elsecase
	include "sound/ps2.pcm_drums.asm" ; else, use the US release's pcm drum samples
	endcase
	restore		; restore 68000 CPU settings
	padding off	; padding is not restored, so we need to set the flag again
	!org (PCMDrums+PCMDrumsEnd-PCMDrumsStart)		; PC must be set to the correct value, so it's the whole code up until the PCMDrums label + the whole z80 code

	if revision=3
	
	if bugfixes=1

